<template>  <div class="majorCascader">    <span v-if="mode === 'text'" v-text="tempData"></span>    <el-cascader        v-if="mode === 'select'"        @change="currentSelectChange"        v-model="tempData"        :options="options"        :props="{ expandTrigger: 'hover' }"    >    </el-cascader>  </div></template><script>import propMixins from './mixin'export default {  name: 'magorCascader',  mixins: [propMixins],  props: {    value: {      type: Object,      require: true    },    options: {      type: [Object, Array]    },    /*     斜模式     此模式不会将组件选择结果赋值到 templateArray 上, 对应要传 xie property     会将组件选择的值直接复制到父组件的对象身上    * */    openXieMode: {      type: Boolean,      default: false    },    xieProberty: {      type: String    },    /*    赋值模式    打散传值, 会将该组件选择之后的数据一一对应 该 props 属性顺序 */    templateArray: {      type: Array    },    realData: {      // 泛型      type: [String, Array, Object],      require: true    },  },  data() {    return {      tempData: []    }  },  watch: {    realData: {      handler(val) {        if (val) {          this.$set(this, 'tempData', [])          var s = []          if (val[0]) {            s.push(val[0])          }          if (val[1]) {            s.push(val[1])          }          this.$set(this, 'tempData', s)        }      }    }  },  methods: {    _common(val) {      this.$set(this.value, this.templateArray[0], val[0])      if (val[1]) {        this.$set(this.value, this.templateArray[1], val[1] || '')      }    },    currentSelectChange(val) {      this.value[this.templateArray[0]] = ''      this.value[this.templateArray[1]] = ''      var log = console.log.bind(console)      if (this.openXieMode) {        this.$set(this.value, this.xieProberty, val)      } else {        this._common(val)      }    }  },}</script><style scoped></style>